select tsvm.ID, tsvm.INV_TYPE, tsvm.BUYER_TAXNO, tsvm.INV_KIND, tsvm.INV_NUM, tsvm.INV_COST, tsvm.INV_SUM, tsvm.INV_VAT, tsvm.INV_DATE, tsvm.BUYER_ADDR_TEL, tsvm.BUYER_BANK, tsvm.BUYER_NAME, tsvm.SELLER_TAXNO, tsvm.SELLER_NAME, tsvm.SELLER_ADDR_TEL, tsvm.SELLER_BANK, ex.accountTime as accountTimeStr, ex.voucherNum as voucherNum, ex.BILL_CODE as billCode from t_scm_vat_main tsvm LEFT JOIN (select listagg(voucher_num,',') within group(order by voucher_num) voucherNum,listagg(to_char(ACCOUNT_TIME,'yyyy-mm-dd'),',') within group(order by ACCOUNT_TIME) accountTime,inv_kind,inv_num,BILL_CODE from T_SCM_VAT_MAIN_EXTEND where 1=1
and to_char(ACCOUNT_TIME,'yyyy-mm-dd') >= #{accountBeginDate}
and to_char(ACCOUNT_TIME,'yyyy-mm-dd') <= #{accountEndDate}
and VOUCHER_NUM like '%'||#{voucherNum,jdbcType=VARCHAR}||'%'
and BILL_CODE = #{billCode,jdbcType=VARCHAR}
group by inv_kind,inv_num,BILL_CODE ) ex on tsvm.inv_kind = ex.inv_kind and tsvm.INV_NUM = ex.inv_num where not exists (select * from t_s_collect_info s where s.invoice_code = tsvm.inv_kind and s.invoice_number = tsvm.inv_num) and inv_status = '0' and ((inv_confirm_status='0' and inv_dedu_result = '0') or ( inv_confirm_status='3' and inv_dedu_result = '2')) and (inv_type = '01' or inv_type = '03' or (inv_type = '14' and tsvm.tollsign = '06')) and PRE_DEDUCT_FLAG = '1'
and BILL_CODE = #{billCode,jdbcType=VARCHAR}
